    ungz = (data) => ZipUtil.unGzip(data, "");

    //Base64ç¼–ç 
    b64En = (b64) => Base64.encode(b64);

    //Base64è§£ç 
    b64De = (b64) => Base64.decode(b64);

    //ttEncrypt
    function TT(data) {
        const {
            java
        } = this;

        //SHA512åŠ å¯†
        sha = (arr) => DigestUtil.sha512(arr);
        value = RandomUtil.randomBytes(32);
        arr = b64De("TdTC5rgxYgkOUrPHpnM7pByyRiuCmrWKGWs521cXdST0m69/COjWjSanLjfBqVovHwWlGJKu8pSXMrYqOKrdWA==")
        arr = sha(sha(value).concat(arr));
        key = arr.slice(0, 16);
        iv = arr.slice(16, 32);

        data = gzip(data);
        data = sha(data).concat(data);
        data = java.createSymmetricCrypto("AES/CBC/PKCS5Padding", key, iv).encrypt(data);
        return [116, 99, 5, 16, 0, 0].concat(value, data);
    }

    //X-Gorgonå‚æ•°
    function xGorgon(path, params, data, ck) {
        const {  java, source  } = this;
        params = [
            params,
            "aid=1967",
            "channel=0",
            "os_version=0",
            "app_name=novelapp",
            "version_code=99999",
            "device_platform=android",
            source.getLoginHeader() || "",
        ].join("&").split("&").sort().join("&").replace(/^&+/, "");
        if (!data) {
            path = "/reading/bookapi/" + path + "/v/?";
        }
        let url = getHost() + path + params;


        //md5åŠ å¯†
        md5 = (str) => DigestUtil.md5Hex(str);

        //åè½¬å­—ç¬¦ä¸²
        rStr = (str) => StrUtil.reverse(str);

        //10è½¬16è¿›åˆ¶
        Hex = (num) => num.toString(16).padStart(2, "0");

        //åè½¬16è¿›åˆ¶
        rHex = (num) => parseInt(rStr(Hex(num)), 16);

        //åè½¬2è¿›åˆ¶
        function rBin(num) {
            let bin = num.toString(2).padStart(8, "0");
            return parseInt(rStr(bin), 2);
        }



        //è¿ç®—
        function getHex(ck) {
            let hex = md5(params)
            hex += data ? md5(data) : "0".repeat(8)
            hex += ck ? md5(ck) : "0".repeat(8)
            return hex
        }

        function calculate(hex, ck) {
            let len = 0x14;
            let key = [0xDF, 0x77, 0xB9, 0x40, 0xB9, 0x9B, 0x84, 0x83, 0xD1, 0xB9, 0xCB, 0xD1, 0xF7, 0xC2, 0xB9, 0x85, 0xC3, 0xD0, 0xFB, 0xC3];
            let paramList = [];
            for (let i = 0; i < 9; i += 4) {
                let temp = hex.substring(8 * i, 8 * (i + 1));
                for (let j = 0; j < 4; j++) {
                    let h = parseInt(temp.substring(j * 2, (j + 1) * 2), 16);
                    paramList.push(h);
                }
            }
            paramList.push(0x0, 0x6, 0xB, 0x1C);
            let T = Math.floor(Date.now() / 1000);
            paramList.push((T >> 24) & 0xFF, (T >> 16) & 0xFF, (T >> 8) & 0xFF, T & 0xFF);
            //å¼‚æˆ–
            let eorResultList = []
            for (let i = 0; i < paramList.length; i++) {
                eorResultList.push(paramList[i] ^ key[i % len]);
            }
            for (let A, B, C, D, i = 0; i < len; i++) {
                A = rHex(eorResultList[i]);
                B = eorResultList[(i + 1) % len];
                C = rBin(A ^ B);
                D = ((C ^ 0xFFFFFFFF) ^ len) & 0xFF;
                eorResultList[i] = D;
            }
            let result = ""
            for (let param of eorResultList) {
                result += Hex(param);
            }
            let option = {
                "headers": {
                    "X-Khronos": String(T),
                    "X-Gorgon": "0404b0d30000" + result,
                    "User-Agent": "com.dragon.read",
                    "Cookie": ck ? ck : ""
                }
            }
            let b_ = option
            b_["body"] = String(data ? data : "")
            java.log(url + "," + JSON.stringify(b_))
            if (data) {
                return post(url, data, option.headers).data;
            } else {
                return url + "," + JSON.stringify(option);
            }
        }
       return  calculate(getHex(ck), ck);
    }
}

